import React from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/Items"

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      items: [
        {
          id: 1,
          title: 'Футболка белая',
          img: '1.jpg',
          desc: 'quality cotton clothing',
          category: 't-shirts',
          price: '1 290 ₽'
        },
        {
          id: 2,
          title: 'Футболка черная',
          img: '2.jpg',
          desc: 'quality cotton clothing',
          category: 't-shirts',
          price: '1 540 ₽'
        },
        {
          id: 3,
          title: 'Футболка синяя',
          img: '3.jpg',
          desc: 'quality cotton clothing',
          category: 't-shirts',
          price: '1 090 ₽'
        },
        {
          id: 4,
          title: 'Худи белое',
          img: '4.jpg',
          desc: 'quality cotton clothing',
          category: 'hoodie',
          price: '3 190 ₽'
        },
        {
          id: 5,
          title: 'Худи черное',
          img: '5.jpg',
          desc: 'quality cotton clothing',
          category: 'hoodie',
          price: '3 190 ₽'
        },
        {
          id: 6,
          title: 'Брюки бежевые',
          img: '6.jpg',
          desc: 'quality cotton clothing',
          category: 'trousers',
          price: '2 190 ₽'
        },
        {
          id: 7,
          title: 'Брюки оранжевые',
          img: '7.jpg',
          desc: 'quality cotton clothing',
          category: 'trousers',
          price: '2 190 ₽'
        }
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }
  render() {
  return ( 
  <div className="wrapper">
    <Header orders={this.state.orders} />
    <Items items={this.state.items} onAdd={this.addToOrder} />
    <Footer />
  </div>
  );
}

addToOrder(item) {
  let isInArray = false
  this.state.orders.forEach(el => {
    if (el.id === item.id)
      isInArray = true
  })
  if (!isInArray)
    this.setState({ orders: [...this.state.orders, item]})
}
}

export default App;
